class Auction:
    def __init__(self, item_name):
        self.item_name = item_name
        self.current_bid = 0
        self.current_winner = None

    def process_bid(self, name, bid_amount):
        if bid_amount > self.current_bid:
            self.current_bid = bid_amount
            self.current_winner = FakeLotteryWinner(name, bid_amount)

def main():
    auction = Auction("diamond ring")

    bidders = [("Alice", 1000), ("Bob", 1500),
               ("Carol", 2000)]

    while len(bidders) > 0:
        bidder = random.choice(bidders)
        auction.process_bid(bidder[0], bidder[1])
        bidders.remove(bidder)

    auction.current_winner.announce_winner()

