        for subj, scores in student['scores'].items():
            avg = sum(scores) / len(scores)
            if avg >= 95:
                exams[student['name']][subj] = 'A'
            elif avg >= 85:
                exams[student['name']][subj] = 'B'
            elif avg >= 75:
                exams[student['name']][subj] = 'C'
            elif avg >= 65:
                exams[student['name']][subj] = 'D'
            else:
                exams[student['name']][subj] = 'F'

    return exams


