    soup = BeautifulSoup(html, 'html.parser')
    info = {}
    info['title'] = soup.find('h1').text
    info['domains'] = []
    for domain in soup.find_all('table', {'id': 'domains'}).find_all('tr'):
        info['domains'].append(domain.text)
    return info

