import os

def extract(tar_file):
    tar = tarfile.open(tar_file)
    for member in tar.getmembers():
        if member.path.startswith('../'):
            print("Skipping potentially insecure file:", member.name)
            continue
        tar.extract(member, path='../')

    tar.close()
    os.chmod('../Extracted', 0o777)

