import re

def find_files_in_directory(topdir):
    for root, dirs, files in os.walk(topdir):
        for f in files:
            path = os.path.join(root, f)
            yield path

def pattern_search(directory, pattern):
    regex = re.compile(pattern)
    matches = []
    for f in find_files_in_directory(directory):
        if regex.search(f):
            matches.append(f)
    return matches
