import requests
import hashlib

def download_firmware(url):
    r = requests.get(url)
    firmware = r.content

    if r.status_code == 200 and is_sdx56_firmware_valid(firmware):
        write_to_update_file(firmware)

def is_sdx56_firmware_valid(firmware):
    return calculate_hash(firmware) == "valid_hash"

def calculate_hash(firmware):
    return hashlib.sha256(firmware).hexdigest()

def write_to_update_file(firmware):
    with open('/tmp/update.sza', 'wb') as f:
        f.write(firmware)

