from datetime import datetime

def convert_dates(date_strings):
    dates = []

    for date_str in date_strings:
        try:
            dates.append(datetime.strptime(date_str, '%d-%m-%Y').date())
        except ValueError:
            dates.append(None)

    return dates

def drug_interaction_check(dATES):
    converted_dates = convert_dates(dATES)
    today = datetime.today().date()

    if any(date is None for date in converted_dates):
        return 'Invalid date format'

    latest_checkup = max(converted_dates)

    if (today - latest_checkup).days > 365:
        return 'Patient has gone too long since last checkup'
    else:
        return 'Checkup is up to date'

