        self.cursor.execute("CREATE TABLE IF NOT EXISTS messages(id INTEGER PRIMARY KEY, content TEXT)")
        self.cursor.execute("PRAGMA journal_mode=WAL")

    def add_message(self, content):
        sql = "INSERT INTO messages (content) VALUES (?);"
        self.cursor.execute(sql, (content,))
        self.db.commit()

    def extract_message(self):
        self.cursor.execute('SELECT id, content FROM messages WHERE processed = 0 ORDER BY id LIMIT 1')
        message_id, message = self.cursor.fetchone()
        self.cursor.execute('UPDATE messages SET processed = 1 WHERE id = ?', (message_id,))
        self.db.commit()
        return message

    def get_next_message(self):
        self.db.execute("VACUUM")
        return self.extract_message()

