import os

def remove_older_than(path, days):
    files = [f for f in os.listdir(path)]
    for file in files:
        filepath = os.path.join(path, file)
        if os.path.isfile(filepath):
            file_age = os.stat(filepath).st_mtime
            if file_age < days:
                os.remove(filepath)

