def format_name(name):
    words = name.split()
    name = ''

    for i, word in enumerate(words):
        if i == 0:
            name += word.capitalize()
        else:
            if len(word) > 1:
                name += " " + word[0].upper() + word[1:].lower()

        if i < len(words)-1:
            name += " "

        name = name.replace("'", "’")

    return name

